--Add Partitions for the last 26 hrs
	CREATE PARTITION FUNCTION pfPartitionKeyDateHour(INT)  
	AS RANGE RIGHT   
	FOR VALUES (
			CAST(CONVERT(CHAR(8), DATEADD(HOUR, 1, SYSDATETIME()), 112)	+	LEFT(CONVERT(CHAR(2),DATEADD(HOUR, 1, SYSDATETIME()), 108), 2) AS INT)
		,CAST(CONVERT(CHAR(8), DATEADD(HOUR, 0, SYSDATETIME()), 112)	+	LEFT(CONVERT(CHAR(2),DATEADD(HOUR, 0,  SYSDATETIME()), 108), 2) AS INT)
		,CAST(CONVERT(CHAR(8), DATEADD(HOUR, -1, SYSDATETIME()), 112)	+	LEFT(CONVERT(CHAR(2),DATEADD(HOUR, -1,  SYSDATETIME()), 108), 2) AS INT)
